"use client";

import React, { useEffect, useState, useRef } from "react";
import { createClient } from "@/utils/supabase/client";
import uploadProfilePicture from "@/utils/uploadProfilePicture";

import styles from "./LecturerDashboard.module.css";
import { useRouter } from "next/navigation";

import Image from "next/image";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Sheet,
  SheetClose,
  SheetContent,
  SheetDescription,
  SheetFooter,
  SheetHeader,
  SheetTitle,
  SheetTrigger,
} from "@/components/ui/sheet";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Skeleton } from "@/components/ui/skeleton";
import { ChevronsUp, ChevronsDown, LogOut } from "lucide-react";
import { ChartContainer, ChartTooltipContent } from "@/components/ui/chart";

import {
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Bar,
  BarChart,
} from "recharts";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Switch } from "@/components/ui/switch";
import useUserStore from "@/stores/useUserStore";
import {
  Drawer,
  DrawerTrigger,
  DrawerContent,
  DrawerHeader,
  DrawerTitle,
  DrawerDescription,
  DrawerFooter,
  DrawerClose,
} from "@/components/ui/drawer";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
} from "@/components/ui/command";
import { ScrollArea } from "@/components/ui/scroll-area";
import Link from "next/link";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

const LecturerDashboard = () => {
  const supabase = createClient(); //database

  const [isLoading, setIsLoading] = useState(true);
  const { user, clearUser } = useUserStore();
  const userId = user?.user_id;
  const router = useRouter();

  interface UserInfo {
    user_id: string;
    username: string;
    role?: string;
    pfp_url: string;
    email: string;
  }

  const [userInfo, setUserInfo] = useState<UserInfo | null>(null);
  const [newUsername, setNewUsername] = useState(userInfo?.username || "");
  const [usernameError, setUsernameError] = useState("");
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [newPfpUrl, setNewPfpUrl] = useState("");
  useState(false);

  const [showConfirmation, setShowConfirmation] = useState(false);

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  useEffect(() => {
    const fetchUserData = async () => {
      setIsLoading(true); // Set loading to true when fetching starts
      if (!userId) {
        setIsLoading(false); // Ensure loading is false if userId is invalid
        return;
      }

      try {
        const { data, error } = await supabase
          .from("users")
          .select("user_id, username, role, pfp_url, email ")
          .eq("user_id", userId)
          .single();

        if (error) {
          console.error("Error fetching user data:", error);
        } else if (data) {
          setUserInfo(data);
          setNewUsername(data.username);
          setNewPfpUrl(data.pfp_url);
        }
      } finally {
        setIsLoading(false); // Ensure loading is set to false regardless of success or failure
      }
    };

    fetchUserData();
  }, [userId]);
  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  const [userBadgesInfo, setUserBadgesInfo] = useState<
    { url: string; name: string }[]
  >([]);

  const handleSignOut = async () => {
    await supabase.auth.signOut();
    clearUser();
    router.push("/sign-in");
  };

  const handleBadges = async () => {
    setIsLoading(true);
    try {
      const { data: userBadgeData, error: userBadgeError } = await supabase
        .from("user_badges")
        .select("badge_id")
        .eq("user_id", userId);

      if (userBadgeError) {
        console.error("Error fetching user badges:", userBadgeError);
        setIsLoading(false);
        return;
      }

      if (userBadgeData && userBadgeData.length > 0) {
        const badgeIds = userBadgeData.map((userBadge) => userBadge.badge_id);

        const { data: badgesData, error: badgesError } = await supabase
          .from("badges")
          .select("image_url, name")
          .in("badge_id", badgeIds);

        if (badgesError) {
          console.error("Error fetching badge URLs:", badgesError);
        } else if (badgesData) {
          const badgeInfos = badgesData.map((badge) => ({
            url: badge.image_url,
            name: badge.name,
          }));
          setUserBadgesInfo(badgeInfos);
        }
      } else {
        setUserBadgesInfo([]);
      }
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    handleBadges();
  }, [userId]);

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  const handleEditProfile = async () => {
    let hasError = false;
    const usernameRegex = /^[a-zA-Z0-9_]+$/;

    if (!newUsername.trim()) {
      setUsernameError("Username cannot be empty.");
      hasError = true;
    } else if (!usernameRegex.test(newUsername)) {
      setUsernameError(
        "Username can only contain letters, numbers, and underscores.",
      );
      hasError = true;
    } else {
      setUsernameError("");
    }

    if (hasError) {
      return;
    }

    const updates: {
      username?: string;
      pfp_url?: string;
    } = {};
    if (newUsername !== user?.username) updates.username = newUsername;
    if (newPfpUrl !== user?.pfp_url && newPfpUrl) updates.pfp_url = newPfpUrl;

    if (Object.keys(updates).length === 0) {
      alert("No changes to save.");
      return;
    }

    const { error } = await supabase
      .from("users")
      .update(updates)
      .eq("user_id", userId);

    if (error) {
      alert("This username already used by others user");
    } else {
      setShowConfirmation(true);
      if (Object.keys(updates).length > 0) {
      }
      setNewPfpUrl("");
    }
  };

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  const [publishedQuizCount, setPublishedQuizCount] = useState<number | null>(
    null,
  );
  const [isLoadingPublishedQuizzes, setIsLoadingPublishedQuizzes] =
    useState(true);
  const [publishedQuizError, setPublishedQuizError] = useState<string | null>(
    null,
  );

  const fetchPublishedQuizCount = async () => {
    setIsLoadingPublishedQuizzes(true);
    setPublishedQuizError(null);

    try {
      const { data, error, count } = await supabase
        .from("quizzes")
        .select("*", { count: "exact" })
        .eq("user_id", userId);

      if (error) {
        console.error("Error fetching published quizzes:", error);
        setPublishedQuizError(error.message);
        setPublishedQuizCount(null);
      } else {
        setPublishedQuizCount(count);
      }
    } finally {
      setIsLoadingPublishedQuizzes(false);
    }
  };

  useEffect(() => {
    fetchPublishedQuizCount();
  }, [userId]);

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  const chartConfig = {
    attempts: {
      label: "Attempts",
      color: "hsl(var(--chart-1))",
    },
  };

  interface QuizAttemptData {
    name: string;
    attempts: number;
  }

  const [chartData, setChartData] = useState<QuizAttemptData[]>([]);
  const [totalAttemptsAllQuizzes, setTotalAttemptsAllQuizzes] =
    useState<number>(0);

  useEffect(() => {
    const fetchQuizAttemptData = async () => {
      try {
        const { data: quizzesData, error: quizzesError } = await supabase
          .from("quizzes")
          .select("quiz_id, name")
          .eq("user_id", userId);

        if (quizzesError) {
          console.error("Error fetching quizzes:", quizzesError);
          return;
        }

        if (!quizzesData || quizzesData.length === 0) {
          setChartData([]);
          setTotalAttemptsAllQuizzes(0);
          return;
        }

        const quizIds = quizzesData.map((quiz) => quiz.quiz_id);
        const quizIdNameMap: { [key: string]: string } = quizzesData.reduce(
          (acc: { [key: string]: string }, quiz) => {
            acc[quiz.quiz_id] = quiz.name;
            return acc;
          },
          {},
        );

        const { data: attemptsData, error: attemptsError } = await supabase
          .from("quiz_attempts")
          .select("quiz_id")
          .in("quiz_id", quizIds);

        if (attemptsError) {
          console.error("Error fetching quiz attempts:", attemptsError);
          return;
        }

        if (!attemptsData) {
          setChartData([]);
          setTotalAttemptsAllQuizzes(0);
          return;
        }

        const quizAttemptCounts: { [quizId: string]: number } = {};
        attemptsData.forEach((attempt) => {
          quizAttemptCounts[attempt.quiz_id] =
            (quizAttemptCounts[attempt.quiz_id] || 0) + 1;
        });

        const formattedData: QuizAttemptData[] = Object.keys(
          quizAttemptCounts,
        ).map((quizId) => ({
          name: quizIdNameMap[quizId] || "Unknown Quiz",
          attempts: quizAttemptCounts[quizId],
        }));

        setChartData(formattedData);
        setTotalAttemptsAllQuizzes(attemptsData.length);
      } catch (error) {
        console.error("Error fetching quiz attempt data:", error);
      }
    };

    fetchQuizAttemptData();
  }, [userId]);

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  interface Quiz {
    quiz_id: string;
    user_id: string;
    name: string;
    description: string;
    public_visibility: boolean;
    join_code: string;
    quiz_cover_url: string;
    attemptCount?: number; // Ensure this is here
  }

  const [publishedQuizzes, setPublishedQuizzes] = useState<Quiz[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [selectedQuiz, setSelectedQuiz] = useState<Quiz | null>(null);
  const [editQuizName, setEditQuizName] = useState("");
  const [editQuizDescription, setEditQuizDescription] = useState("");
  const [deleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
  const [quizToDelete, setQuizToDelete] = useState<string | null>(null);
  const [deletionNotificationOpen, setDeletionNotificationOpen] =
    useState(false);

  const fetchQuizAttemptCount = async (quizId: string): Promise<number> => {
    try {
      const { count, error } = await supabase
        .from("quiz_attempts")
        .select("*", { count: "exact" })
        .eq("quiz_id", quizId);

      if (error) {
        console.error("Error fetching quiz attempt count:", error);
        return 0;
      }
      return count || 0;
    } catch (error: any) {
      console.error("An unexpected error occurred:", error);
      return 0;
    }
  };

  const fetchPublishedQuizzes = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const { data, error } = await supabase
        .from("quizzes")
        .select("*")
        .eq("user_id", userId);

      if (error) {
        console.error("Error fetching published quizzes:", error);
        setError(error.message);
      } else if (data) {
        const quizzesWithCounts = await Promise.all(
          data.map(async (quiz) => {
            const attemptCount = await fetchQuizAttemptCount(quiz.quiz_id);
            return { ...quiz, attemptCount };
          }),
        );
        setPublishedQuizzes(quizzesWithCounts);
      }
    } catch (err: any) {
      console.error("An unexpected error occurred:", err);
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchPublishedQuizzes();
  }, [userId]);

  const handleVisibilityChange = async (quizId: string, isPublic: boolean) => {
    try {
      const { error } = await supabase
        .from("quizzes")
        .update({ public_visibility: !isPublic })
        .eq("quiz_id", quizId);

      if (error) {
        console.error("Error updating quiz visibility:", error);
      } else {
        setPublishedQuizzes((prevQuizzes) =>
          prevQuizzes.map((quiz) =>
            quiz.quiz_id === quizId
              ? { ...quiz, public_visibility: !isPublic }
              : quiz,
          ),
        );
      }
    } catch (err: any) {
      console.error("An unexpected error occurred:", err);
    }
  };

  const handleEditClick = (quiz: Quiz) => {
    setSelectedQuiz(quiz);
    setEditQuizName(quiz.name);
    setEditQuizDescription(quiz.description);
    setEditDialogOpen(true);
  };

  const handleEditSave = async () => {
    if (!selectedQuiz) return;
    try {
      const { error } = await supabase
        .from("quizzes")
        .update({ name: editQuizName, description: editQuizDescription })
        .eq("quiz_id", selectedQuiz.quiz_id);

      if (error) {
        console.error("Error updating quiz:", error);
      } else {
        setPublishedQuizzes((prevQuizzes) =>
          prevQuizzes.map((quiz) =>
            quiz.quiz_id === selectedQuiz.quiz_id
              ? {
                  ...quiz,
                  name: editQuizName,
                  description: editQuizDescription,
                }
              : quiz,
          ),
        );
        setEditDialogOpen(false);
        setSelectedQuiz(null);
      }
    } catch (err: any) {
      console.error("An unexpected error occurred:", err);
    }
  };

  const handleDeleteClick = (quizId: string) => {
    setQuizToDelete(quizId);
    setDeleteConfirmationOpen(true);
  };

  const confirmDeleteQuiz = async () => {
    if (!quizToDelete) return;
    try {
      const { error } = await supabase
        .from("quizzes")
        .delete()
        .eq("quiz_id", quizToDelete);

      if (error) {
        console.error("Error deleting quiz:", error);
      } else {
        setPublishedQuizzes((prevQuizzes) =>
          prevQuizzes.filter((quiz) => quiz.quiz_id !== quizToDelete),
        );
        setDeleteConfirmationOpen(false);
        setQuizToDelete(null);
        setDeletionNotificationOpen(true);
      }
    } catch (err: any) {
      console.error("An unexpected error occurred:", err);
    }
  };

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  interface AttemptWithUser {
    user_id: string;
    correct_questions: number;
    total_questions: number;
    users: { username: any; pfp_url?: any } | any;
  }

  interface QuizAttempt {
    quiz_id: string;
    user_id: string;
    correct_questions: number;
    total_questions: number;
  }

  const [isAttemptsDrawerOpen, setIsAttemptsDrawerOpen] = useState(false);
  const [currentQuizAttemptsWithUser, setCurrentQuizAttemptsWithUser] =
    useState<AttemptWithUser[] | null>(null);
  const [selectedQuizForAttempts, setSelectedQuizForAttempts] =
    useState<Quiz | null>(null);
  const [searchAttemptText, setSearchAttemptText] = useState("");
  const [attemptCounts, setAttemptCounts] = useState<Record<string, number>>(
    {},
  );

  const fetchQuizAttemptsWithUser = async (quizId: string) => {
    try {
      const { data, error } = await supabase
        .from("quiz_attempts")
        .select(
          "user_id, correct_questions, total_questions, users(username, pfp_url)",
        ) // Select pfp_url as well
        .eq("quiz_id", quizId);

      if (error) {
        console.error("Error fetching quiz attempts with user:", error);
        return null;
      }
      return data;
    } catch (error: any) {
      console.error("An unexpected error occurred:", error);
      return null;
    }
  };

  const fetchAllQuizAttempts = async (
    quizId: string,
  ): Promise<QuizAttempt[] | null> => {
    try {
      const { data, error } = await supabase
        .from("quiz_attempts")
        .select("*")
        .eq("quiz_id", quizId);

      if (error) {
        console.error("Error fetching all quiz attempts:", error);
        return null;
      }
      return data as QuizAttempt[] | null;
    } catch (error: any) {
      console.error("An unexpected error occurred:", error);
      return null;
    }
  };

  //////////////////////////////////////////////////////////////////////////////////////////////////////////////

  return (
    <div className={styles.all}>
      <div className={styles.pageHeader}>
        {/* ============================================================================================================ */}
        <div>
          {isLoading ? (
            <div className="flex items-center space-x-4 border-b border-gray-200 p-2">
              <Skeleton className="h-12 w-12 rounded-full" />
              <div className="space-y-2">
                <Skeleton className="h-4 w-[200px]" />
                <Skeleton className="h-4 w-[150px]" />
              </div>
            </div>
          ) : user ? (
            <div className="flex items-center gap-4 border-b border-gray-200 p-2">
              <Avatar>
                <AvatarImage src={user.pfp_url} alt={user.username} />
                <AvatarFallback>
                  {user.username.slice(0, 2).toUpperCase()}
                </AvatarFallback>
              </Avatar>
              <div className="flex items-start space-x-2">
                <div>
                  <p className="font-bold">
                    {user.username || "No User Found"}
                  </p>
                  {user.role && (
                    <Badge className="bg-[#F6F8D5] hover:bg-[#CDCFB2]">
                      {user.role}
                    </Badge>
                  )}
                </div>
                <div>
                  <Badge className={styles.badgesContainer}>
                    {userBadgesInfo.length > 0 &&
                      userBadgesInfo.map((badgeInfo, index) => (
                        <div
                          key={index}
                          className="relative h-4 w-4 overflow-hidden rounded-full"
                        >
                          <Image
                            src={badgeInfo.url}
                            alt={`Badge ${index + 1}`}
                            layout="fill"
                            objectFit="cover"
                            title={badgeInfo.name}
                          />
                        </div>
                      ))}
                  </Badge>
                </div>
              </div>
            </div>
          ) : (
            <p>No User Found</p>
          )}
        </div>

        {/* ============================================================================================================ */}

        <section className="flex flex-row gap-2">
          <div className={styles.settingButton}>
            <AlertDialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
              <AlertDialogTrigger asChild>
                <Button variant="outline">
                  <LogOut className="h-4 w-4" />
                </Button>
              </AlertDialogTrigger>
              <AlertDialogContent className="z-[10000]">
                <AlertDialogHeader>
                  <AlertDialogTitle>
                    Are you sure you want to sign out?
                  </AlertDialogTitle>
                  <AlertDialogDescription>
                    This will log you out of your current session.
                  </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                  <AlertDialogCancel onClick={() => setIsDialogOpen(false)}>
                    Cancel
                  </AlertDialogCancel>
                  <AlertDialogAction onClick={handleSignOut}>
                    Yes, Sign Out
                  </AlertDialogAction>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialog>
          </div>

          {/* ============================================================================================================ */}

          <div className={styles.settingButton}>
            <Sheet>
              <SheetTrigger asChild>
                <Button variant="outline">
                  <img
                    src="data:image/png;base64,"
                    alt="Settings"
                  />
                </Button>
              </SheetTrigger>
              <SheetContent
                style={{
                  overflowY: "scroll",
                  backgroundColor: "#1B496C",
                  color: "#F6F8D5",
                }}
              >
                <SheetHeader>
                  <SheetTitle className={styles.settingTitle}>
                    Edit Profile
                    <img
                      src="data:image/png;base64,"
                      alt="Settings Icon"
                      className="h-5 w-5"
                    />
                  </SheetTitle>

                  <SheetDescription>
                    Make changes to your profile here. Click save when you're
                    done.
                  </SheetDescription>
                </SheetHeader>
                <div className="grid gap-4 py-4">
                  <div className="grid gap-2">
                    <Label htmlFor="profilePicture" className="text-left">
                      Profile Picture
                    </Label>
                    <div className="flex items-center space-x-4">
                      <Avatar>
                        <AvatarImage
                          src={newPfpUrl || user?.pfp_url}
                          alt={user?.username}
                        />
                        <AvatarFallback>
                          {user?.username?.slice(0, 2)?.toUpperCase() || "?"}
                        </AvatarFallback>
                      </Avatar>
                      <div className={styles.uploadProfilePictureButton}>
                        <Input
                          type="file"
                          id="profilePicture"
                          accept="image/*"
                          className="hidden"
                          onChange={async (e) => {
                            if (e.target.files && e.target.files.length > 0) {
                              const file = e.target.files[0];
                              const newUrl = await uploadProfilePicture(file);
                              if (newUrl) {
                                setNewPfpUrl(newUrl);
                              } else {
                                console.error(
                                  "Failed to upload profile picture.",
                                );
                              }
                            }
                          }}
                        />
                        <Label
                          htmlFor="profilePicture"
                          className="cursor-pointer rounded-md border bg-[#427C83] px-4 py-2 font-semibold transition-colors duration-150 hover:bg-[#356369]"
                        >
                          Upload Profile Picture
                        </Label>
                      </div>
                    </div>
                    {newPfpUrl && (
                      <p className="mt-1 text-xs text-gray-500">
                        New profile picture will be updated after "Save
                        Changes".
                      </p>
                    )}
                  </div>

                  <div className="grid gap-2">
                    <Label htmlFor="username" className="text-left">
                      Username
                    </Label>
                    <Input
                      id="username"
                      className="col-span-3"
                      value={newUsername}
                      onChange={(e) =>
                        setNewUsername(e.target.value.replace(/\s/g, ""))
                      }
                      style={{ backgroundColor: "#427C83" }}
                    />
                  </div>
                </div>

                <Link href="/protected/reset-password" className="underline">
                  Change Password
                </Link>

                <SheetFooter className="mt-3 flex flex-col items-end justify-between gap-2 md:flex-row md:items-center md:justify-end md:gap-0">
                  <div className="mb-2 flex items-center space-x-2 md:mb-0">
                    <SheetClose asChild>
                      <Button>Close</Button>
                    </SheetClose>
                    <Button type="submit" onClick={handleEditProfile}>
                      Save Changes
                    </Button>
                  </div>
                  <div className="relative">
                    <div
                      className="bg-grey pointer-events-none absolute inset-y-0 left-[-8px] h-full w-0.5 md:bottom-auto md:left-[-8px] md:top-auto md:h-auto md:w-full md:bg-transparent"
                      style={{ height: "35px" }}
                    />
                  </div>
                </SheetFooter>
              </SheetContent>

              {showConfirmation && (
                <Sheet
                  open={showConfirmation}
                  onOpenChange={setShowConfirmation}
                >
                  <SheetContent>
                    <SheetHeader>
                      <SheetTitle>Changes Saved</SheetTitle>
                      <SheetDescription>
                        Your profile has been updated successfully.
                      </SheetDescription>
                    </SheetHeader>
                    <SheetFooter>
                      <SheetClose asChild>
                        <Button
                          onClick={() => {
                            setShowConfirmation(false);
                            window.location.reload();
                          }}
                        >
                          OK
                        </Button>
                      </SheetClose>
                    </SheetFooter>
                  </SheetContent>
                </Sheet>
              )}
            </Sheet>
          </div>
        </section>
      </div>
      {/* ============================================================================================================ */}
      <div className={styles.dashContent}>
        <div className={styles.dashboardStats}>
          <div className={styles.badgesEarned}>
            <h1>You have Earned: </h1>
            {userBadgesInfo ? (
              <>
                <div className={styles.badges}>
                  <h2>{userBadgesInfo.length}</h2>
                  <img
                    src="data:image/png;base64,"
                    alt="Badged Icon"
                    className="h-10 w-10"
                  />
                </div>

                <p>in your life time</p>
              </>
            ) : (
              <>
                <h3>"No badges... yet!"</h3>
              </>
            )}
          </div>

          {/* ============================================================================================================ */}

          <div className={styles.quizPublished}>
            <h1>Total Quizzes Published</h1>
            {isLoadingPublishedQuizzes ? (
              <h2>Loading...</h2>
            ) : publishedQuizError ? (
              <p>Error loading quiz count: {publishedQuizError}</p>
            ) : (
              <h2>{publishedQuizCount !== null ? publishedQuizCount : 0}</h2>
            )}
          </div>
        </div>

        {/* ============================================================================================================ */}
        <div className={styles.graphContainer}>
          <Card
            style={{
              backgroundColor: "#7FAFA0",
              border: "2px solid lightblue",
            }}
          >
            <CardHeader>
              <CardTitle style={{ color: "#6B8474" }}>
                Quiz Attempt Chart
              </CardTitle>
              <CardDescription style={{ color: "#F6F8D5" }}>
                Total attempts across all your quizzes
              </CardDescription>
            </CardHeader>

            <CardContent>
              <ChartContainer
                config={chartConfig}
                style={{ width: "100%", height: "170px" }}
              >
                <ResponsiveContainer>
                  <BarChart accessibilityLayer data={chartData}>
                    <CartesianGrid vertical={false} />
                    <XAxis
                      dataKey="name"
                      tick={{ fontSize: 10, stroke: "#E8E9CC" }}
                      angle={-30}
                      textAnchor="end"
                    />
                    <YAxis tick={{ stroke: "#E8E9CC" }} />
                    <Tooltip cursor={false} content={<ChartTooltipContent />} />
                    <Bar dataKey="attempts" fill="#4F959D" radius={8} />
                  </BarChart>
                </ResponsiveContainer>
              </ChartContainer>
            </CardContent>
            <CardFooter className="flex-col items-start gap-2 text-sm">
              <div className="leading-none text-[#fff] text-muted-foreground">
                Total of {totalAttemptsAllQuizzes} attempts across all your
                quizzes.
              </div>
            </CardFooter>
          </Card>
        </div>
      </div>

      {/* ============================================================================================================ */}

      <div className={styles.recentPublishedQuiz}>
        <h1>Your Published Quizzes</h1>
        <div className={styles.recentPublishedQuizContainer}>
          {isLoading ? (
            <div>Loading published quizzes...</div>
          ) : error ? (
            <div>Error loading quizzes: {error}</div>
          ) : publishedQuizzes.length > 0 ? (
            publishedQuizzes.map((quiz) => (
              <div
                key={quiz.quiz_id}
                className={styles.singleRecentPublishedQuiz}
                onMouseEnter={(e) => {
                  if (quiz.quiz_cover_url) {
                    e.currentTarget.style.backgroundImage = `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url('${quiz.quiz_cover_url}')`;
                    e.currentTarget.classList.add(styles.showCover);
                  }
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.backgroundImage = "none";
                  e.currentTarget.classList.remove(styles.showCover);
                }}
              >
                <div className={styles.nameEditDelete}>
                  <h1>{quiz.name}</h1>
                  <div className={styles.editDelete}>
                    <Button
                      variant="outline"
                      onClick={() => handleEditClick(quiz)}
                    >
                      Edit
                    </Button>
                    <Button
                      variant="destructive"
                      onClick={() => handleDeleteClick(quiz.quiz_id)}
                    >
                      Delete
                    </Button>
                  </div>
                </div>

                <div className={styles.desCode}>
                  <p>{quiz.description}</p>
                  <Badge variant="outline" className={styles.joinCode}>
                    {quiz.join_code}
                  </Badge>
                </div>

                <div className={styles.attemptPeopleVisibility}>
                  <div>
                    <Drawer
                      open={isAttemptsDrawerOpen}
                      onOpenChange={setIsAttemptsDrawerOpen}
                    >
                      <DrawerTrigger asChild>
                        <Button
                          style={{ padding: "1px" }}
                          variant="link"
                          onClick={async () => {
                            const attemptsWithUser =
                              await fetchQuizAttemptsWithUser(quiz.quiz_id);
                            const allAttempts = await fetchAllQuizAttempts(
                              quiz.quiz_id,
                            );

                            if (attemptsWithUser && allAttempts) {
                              const highestScoresByUser: Record<
                                string,
                                AttemptWithUser
                              > = {};
                              const attemptCountsByUser: Record<
                                string,
                                number
                              > = {};

                              allAttempts.forEach((attempt) => {
                                attemptCountsByUser[attempt.user_id] =
                                  (attemptCountsByUser[attempt.user_id] || 0) +
                                  1;
                              });

                              attemptsWithUser.forEach((attempt) => {
                                if (
                                  !highestScoresByUser[attempt.user_id] ||
                                  attempt.correct_questions >
                                    highestScoresByUser[attempt.user_id]
                                      .correct_questions
                                ) {
                                  highestScoresByUser[attempt.user_id] =
                                    attempt;
                                }
                              });

                              setCurrentQuizAttemptsWithUser(
                                Object.values(highestScoresByUser),
                              );
                              setAttemptCounts(attemptCountsByUser);
                              setSelectedQuizForAttempts(quiz);
                              setIsAttemptsDrawerOpen(true);
                            } else {
                              console.log("Could not fetch attempts data.");
                              setCurrentQuizAttemptsWithUser([]);
                              setAttemptCounts({}); // Reset attempt counts
                              setSelectedQuizForAttempts(quiz);
                              setIsAttemptsDrawerOpen(true);
                            }
                          }}
                        >
                          <span className={styles.attemptPeople}>
                            Total Attempt:{" "}
                            {quiz.attemptCount !== undefined
                              ? quiz.attemptCount
                              : "..."}
                          </span>
                        </Button>
                      </DrawerTrigger>

                      <DrawerContent
                        style={{ backgroundColor: "#1B496C", color: "#F6F8D5" }}
                      >
                        <DrawerHeader>
                          <DrawerTitle>Ranking</DrawerTitle>
                          <DrawerDescription style={{ color: "#fff" }}>
                            Attempted Students
                          </DrawerDescription>
                        </DrawerHeader>
                        <div className="p-4">
                          <Command style={{ backgroundColor: "#1B496C" }}>
                            <CommandInput
                              className="rounded-md bg-[#1B496C] px-4 py-2 font-semibold transition-colors duration-150 hover:bg-[#153A56]"
                              placeholder="Search username..."
                              value={searchAttemptText}
                              onValueChange={setSearchAttemptText}
                            />
                            <ScrollArea className="h-[225px] w-[1300px]">
                              <CommandGroup
                                className="duration-60 rounded-md bg-[#1B496C] px-4 py-2 font-semibold transition-colors hover:bg-[#153A56]"
                                heading="Students"
                              >
                                {currentQuizAttemptsWithUser
                                  ?.filter((attempt) =>
                                    attempt?.users?.username
                                      ?.toLowerCase()
                                      .includes(
                                        searchAttemptText.toLowerCase(),
                                      ),
                                  )
                                  .sort(
                                    (a, b) =>
                                      b.correct_questions - a.correct_questions,
                                  )
                                  .map((attempt, index) => (
                                    <CommandItem
                                      key={attempt.user_id}
                                      className={styles.studentRow}
                                      asChild
                                    >
                                      <Link href={`/sign-in`}>
                                        <div className="flex cursor-pointer items-center space-x-2">
                                          <span
                                            style={{
                                              fontWeight: "bold",
                                              fontSize: "17px",
                                            }}
                                          >
                                            #{index + 1}
                                          </span>
                                          {attempt.users?.pfp_url && (
                                            <img
                                              src={attempt.users.pfp_url}
                                              alt={`${attempt.users.username}'s profile`}
                                              className="h-10 w-10 rounded-full object-cover"
                                            />
                                          )}
                                          <div className="flex flex-col">
                                            <span
                                              style={{ fontWeight: "bold" }}
                                            >
                                              {attempt.users?.username ||
                                                "Unknown User"}
                                              {attemptCounts[attempt.user_id] >
                                              1
                                                ? ` ( ${attemptCounts[attempt.user_id]} Attempted )`
                                                : ""}
                                            </span>
                                            <span>
                                              {attempt.correct_questions}{" "}
                                              Correct
                                            </span>
                                          </div>
                                        </div>
                                      </Link>
                                    </CommandItem>
                                  ))}
                                {currentQuizAttemptsWithUser?.filter(
                                  (attempt) =>
                                    attempt?.users?.username
                                      ?.toLowerCase()
                                      .includes(
                                        searchAttemptText.toLowerCase(),
                                      ),
                                ).length === 0 && (
                                  <CommandEmpty>
                                    No students found.
                                  </CommandEmpty>
                                )}
                              </CommandGroup>
                            </ScrollArea>
                          </Command>
                        </div>
                        <DrawerFooter>
                          <DrawerClose asChild>
                            <Button
                              style={{ backgroundColor: "#303030" }}
                              variant="outline"
                            >
                              Close
                            </Button>
                          </DrawerClose>
                        </DrawerFooter>
                      </DrawerContent>
                    </Drawer>
                  </div>

                  <div className={styles.visibility}>
                    <Label htmlFor={`public-${quiz.quiz_id}`}>
                      {quiz.public_visibility ? "Public" : "Private"}
                    </Label>

                    <Switch
                      id={`public-${quiz.quiz_id}`}
                      checked={quiz.public_visibility}
                      onCheckedChange={(checked) =>
                        handleVisibilityChange(
                          quiz.quiz_id,
                          quiz.public_visibility,
                        )
                      }
                    />
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div>You haven't published any quizzes yet.</div>
          )}
        </div>

        <Dialog open={editDialogOpen} onOpenChange={setEditDialogOpen}>
          <DialogContent className="sm:max-w-[425px]">
            <DialogHeader>
              <DialogTitle>Edit Quiz</DialogTitle>
              <DialogDescription>
                Edit the name and description of your quiz.
              </DialogDescription>
            </DialogHeader>
            <div className="grid gap-4 py-4">
              <div className="grid grid-cols-4 items-center gap-4">
                <Label htmlFor="name" className="text-right">
                  Name
                </Label>
                <Input
                  id="name"
                  value={editQuizName}
                  onChange={(e) => setEditQuizName(e.target.value)}
                  className="col-span-3"
                />
              </div>
              <div className="grid grid-cols-4 items-center gap-4">
                <Label htmlFor="description" className="text-right">
                  Description
                </Label>
                <Input
                  id="description"
                  value={editQuizDescription}
                  onChange={(e) => setEditQuizDescription(e.target.value)}
                  className="col-span-3"
                />
              </div>
            </div>
            <DialogFooter>
              <Button
                type="button"
                variant="secondary"
                onClick={() => setEditDialogOpen(false)}
              >
                Cancel
              </Button>
              <Button
                type="button"
                onClick={handleEditSave}
                style={{ marginBottom: "15px" }}
              >
                Save changes
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>

        <Dialog
          open={deleteConfirmationOpen}
          onOpenChange={setDeleteConfirmationOpen}
        >
          <DialogContent className="sm:max-w-[425px]">
            <DialogHeader>
              <DialogTitle>Confirm Delete</DialogTitle>
              <DialogDescription>
                Are you sure you want to delete this quiz? This action cannot be
                undone.
              </DialogDescription>
            </DialogHeader>
            <DialogFooter>
              <Button
                type="button"
                variant="secondary"
                onClick={() => setDeleteConfirmationOpen(false)}
              >
                Cancel
              </Button>
              <Button
                type="button"
                variant="destructive"
                onClick={confirmDeleteQuiz}
                style={{ marginBottom: "15px" }}
              >
                Delete
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>

        <Dialog
          open={deletionNotificationOpen}
          onOpenChange={setDeletionNotificationOpen}
        >
          <DialogContent className="sm:max-w-[425px]">
            <DialogHeader>
              <DialogTitle>Quiz Deleted</DialogTitle>
              <DialogDescription>
                The quiz has been successfully deleted.
              </DialogDescription>
            </DialogHeader>
            <DialogFooter>
              <Button
                type="button"
                onClick={() => setDeletionNotificationOpen(false)}
              >
                OK
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>

      {/* ============================================================================================================ */}
    </div>
  );
};

export default LecturerDashboard;
